//adicionando dinâmicamente no html
produtList.forEach((item, index) => {
    const cards = document.querySelector('#cards');

    // Insere o card dinamicamente
    cards.innerHTML += `
        <div class="card-produt">
            <div class="img-produt">
                <img src="${item.imagem}">
            </div>
            <h3>${item.nome}</h3>
            <span id="preco">R$${item.preco.toFixed(2)}</span>
            <button data-index="${index}" class="comprar">Comprar</button>
        </div>
    `;
});

// Após todos os produtos serem inseridos, agora adiciona o evento para os botões
document.querySelectorAll('.comprar').forEach(botao => {
    botao.addEventListener('click', function() {
        const r = confirm("Você precisa se logar para acessar esse recurso!\nDeseja fazer login?");

        if (r) {
            location.href = 'login.html';
        }
    });
});
